mapper.select:
select

from sk_project where  title  like CONCAT('%',#{arg1},'%')

  and status = #{arg0}

mapper.update:
update sk_project set status = 1
where id in

  #{id}

mapper.select:
select
from sk_project where category_id = #{category_id}
and status = #{status}
order by likes_count desc
order by view_count desc
order by updatetime desc
